import * as React from 'react';
import { Box, Container, Grid} from '@mui/material';
import Navbar from './components/Navbar';
import AccountCard from './components/AccountCard';
import FormAddCard from './components/FormAddCard';
import axios from 'axios';
import { useEffect } from 'react';

function Home(){
    const [values, setValues] = React.useState({
        allAccounts: [],
        displayedAccounts: [],
        searchText: ""
    })

    useEffect(() => {
        axios
        .get('http://localhost:5000/search')
        .then(response => {setValues({
            ...values,
            allAccounts: response.data, 
            displayedAccounts: response.data})})
    }, []);

    const updateScreen = () => {
        axios
        .get('http://localhost:5000/search')
        .then(response => {setValues({
            ...values,
            allAccounts: response.data,
            displayedAccounts: response.data})})
    }

    const pull_data = (e) => {
        const search = e.target.value;
        const searchAccount = [];
        console.log(values.allAccounts)
        if(search != ""){
            console.log("non vuoto")
            values.allAccounts.forEach(account => {
                if(account['Name'].includes(search)) searchAccount.push(account)
            })
            setValues({...values, displayedAccounts: searchAccount});
        }
        else{
            console.log("vuoto")
            setValues({displayedAccounts: values.allAccounts});
        }
    }

    return(
        <Box>
            <Navbar pushData={pull_data}/>
            <Container sx={{mt: 4, mb: 4}}>
                <Grid container justifyContent="center" alignItems="center" spacing={3}>
                    {values.displayedAccounts.map(account => (
                        <AccountCard
                        key={account['ID']}
                        title={account['Name']}
                        web={account['Web']}
                        username={account['User']}
                        password={account['Password']}
                        subheader={account['Web']}
                        />
                    ))}
                </Grid>    
            </Container>
            <FormAddCard handleClickSave={updateScreen}/> 
        </Box>
    );
}
export default Home;
