import * as React from 'react';
import { Box, Container, Grid} from '@mui/material';
import Navbar from './components/Navbar';
import AccountCard from './components/AccountCard';
import FormAddCard from './components/FormAddCard';
import axios from 'axios';
import { useEffect } from 'react';

function Home(){
    const [values, setValues] = React.useState({
        accounts: [],
        searchText: ""
    })

    useEffect(() => {
        axios
        .get('http://localhost:5000/search')
        .then(response => {setValues({accounts: response.data})})
    }, []);

    const updateScreen = () => {
        axios
        .get('http://localhost:5000/search')
        .then(response => {setValues({...values, accounts: response.data})})
    }

    const pull_data = (e) => {
        const search = e.target.value;
        const accounts = [];
        values.accounts.forEach(account => {
            if(account['Name'].contains(search)) accounts.push(account)
        })
        setValues({...values, accounts: accounts})
        console.log(values.accounts)
    }

    return(
        <Box>
            <Navbar pushData={pull_data}/>
            <Container sx={{mt: 4, mb: 4}}>
                <Grid container justifyContent="center" alignItems="center" spacing={3}>
                    {values.accounts.map(account => (
                        <AccountCard
                        key={account['ID']}
                        title={account['Name']}
                        web={account['Web']}
                        username={account['User']}
                        password={account['Password']}
                        subheader={account['Web']}
                        />
                    ))}
                </Grid>    
            </Container>
            <FormAddCard handleClickSave={updateScreen}/> 
        </Box>
    );
}
export default Home;
