import * as React from 'react';
import { Box, Container, Grid} from '@mui/material';
import Navbar from './components/Navbar';
import AccountCard from './components/AccountCard';
import FormAddCard from './components/FormAddCard';
import axios from 'axios';
import { useEffect } from 'react';

function Home(){
    const [values, setValues] = React.useState({
        accounts: [],
        searchText: ""
    })

    useEffect(() => {
        axios
        .get('http://localhost:5000/search')
        .then(response => {Object.assign(values.accounts,response.data)})
        console.log(values)
    }, []);

    async function decryptPassword(encryption) {
        var res = await axios.post('http://localhost:5000/decryptpassword', { 
            password: encryption.password,
            iv: encryption.iv 
        });
        return res;
    };

    /*const pull_data = (data) => {
        console.log(data);
    } */ 
    
    return(
        <Box>
            <Navbar /*func={pull_data}*//>
            <Container sx={{mt: 4, mb: 4}}>
                <Grid container justifyContent="center" alignItems="center" spacing={3}>
                    {values.accounts.map(account => (
                        <AccountCard
                        key={account}
                        title={account['Name']}
                        web={account['Web']}
                        username={account['User']}
                        password={decryptPassword({password: account['Password'], iv: account['IV']})}
                        />
                    ))}
                </Grid>    
            </Container>
            <FormAddCard/> 
        </Box>
    );
}
export default Home;
